// SPDX-License-Identifier: MIT
pragma solidity ^0.5.7;

contract HealthcareAutomation {

    // Define the structure for Medical Data
    struct MedicalData {
        uint timestamp;   // Timestamp of the data entry
        string deviceId;  // ID of the medical IoT device
        uint value;       // Processed data value (cleaned, filtered, aggregated)
    }

    // Threshold value for triggering an action
    uint public thresholdValue = 100;

    // Contract owner address
    address public owner;

    // Mapping to store medical data records
    mapping(uint => MedicalData) public medicalRecords;
    uint public recordCount = 0;

    // Event emitted when data is stored
    event DataStored(uint timestamp, string deviceId, uint value);

    // Event emitted when the threshold is exceeded
    event ThresholdExceeded(string deviceId, uint value, uint timestamp);

    // Constructor to set the contract deployer as the owner
    constructor() public {
        owner = msg.sender;
    }

    // Modifier to restrict functions to the contract owner
    modifier onlyOwner() {
        require(msg.sender == owner, "Only the contract owner can perform this action.");
        _;
    }

    // Function to update the threshold value (only owner can do this)
    function setThresholdValue(uint _thresholdValue) public onlyOwner {
        thresholdValue = _thresholdValue;
    }

    // Function to store the medical data on the blockchain
    function storeMedicalData(string memory _deviceId, uint _value) public {
        // Record the current timestamp
        uint currentTimestamp = now;

        // Store the medical data in the contract's storage
        medicalRecords[recordCount] = MedicalData(currentTimestamp, _deviceId, _value);
        
        // Emit event for data storage
        emit DataStored(currentTimestamp, _deviceId, _value);
        
        // Check if the value exceeds the threshold and trigger an action
        if (_value > thresholdValue) {
            // Emit an event if the threshold is exceeded
            emit ThresholdExceeded(_deviceId, _value, currentTimestamp);
        }

        // Increment the record count
        recordCount++;
    }

    // Function to retrieve medical data by index
    function getMedicalData(uint _index) public view returns (uint, string memory, uint) {
        MedicalData memory data = medicalRecords[_index];
        return (data.timestamp, data.deviceId, data.value);
    }

    // Function to get the total number of records stored
    function getRecordCount() public view returns (uint) {
        return recordCount;
    }
}
